properties.query:
let DisabledParsers=materialize(_GetWatchlist('ASimDisabledParsers') | where SearchKey in ('Any', 'ExcludeASimRegistry') | extend SourceSpecificParser=column_ifexists('SourceSpecificParser','') | distinct SourceSpecificParser);
let imRegistryEventBuiltInDisabled=toscalar('ExcludeASimRegistryEventBuiltIn' in (DisabledParsers) or 'Any' in (DisabledParsers)); 
union isfuzzy=true
  vimRegistryEventEmpty,
  ASimRegistryEventSentinelOne(imRegistryEventBuiltInDisabled or ('ExcludeASimRegistryEventSentinelOne' in (DisabledParsers) )),
  ASimRegistryEventMicrosoft365D(imRegistryEventBuiltInDisabled or ('ExcludeASimRegistryEventMicrosoft365D' in (DisabledParsers) ))
