properties.query:
// SAP - Medium - Critical authorizations assignment - New Authorization Value
// time span for audit events
let TimeAgo= 6h;
// New Assigned Objects
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUR','AUT']); // Authorization/Authorization Profile &B created / changed.
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
let ChangeDocs = SAPChangeDocsLog;
let UsersEmails = SAPUsersHeader()| summarize by User, Email, SystemID, Client | project-rename ClientID = Client;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | extend TimeGenRoleAuth = TimeGenerated;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let usersinRole =
    ChangeDocs
    | where TimeGenerated > ago(TimeAgo)
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | summarize by SystemID, Role, UserAssigned;
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario = ComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario = SimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup  kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntities =
    SAPAuditLog
    | where MessageID in (AuditClasses)
    | where TimeGenerated > ago(TimeAgo)
    | summarize by TimeGenerated, ClientID, TerminalIPv6, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntities) on User
| where abs(datetime_diff('second', TimeGenRoleAuth, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| lookup UsersEmails on SystemID, ClientID, User
| project
TimeGenRoleAuth, SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host, UserAssignedEmail=Email1

let TopN = 120; // A single analytics rule can generate up to 20 alerts. let's make those count
let SelectedSystems =  dynamic(["All Systems"]);//can also do// let SelectedSystems =  dynamic(["S4P", "B4X", "ODT"]);
let SelectedSystemRoles =  dynamic(["Production"]); //can also do// let SelectedSystemRoles =  dynamic(["Production", "UAT"]);
let SelectedSeverities=  dynamic(["High", "Medium"]);//can also do//let SelectedSeverities =  dynamic(["All Severities"])
// get special users for exclusion and in-focus analysis
let SAPUsersGottenVIP = (SAPUsersGetVIP | project-rename User = SAPUser);
// let SelectedRuleTypes = dynamic(["All RuleTypes"]);
let SelectedRuleTypes= dynamic(["AnomaliesOnly"]);
let SAPAuditAnomal = SAPAuditLogAnomalies(LearningTime = 14d, DetectingTime=1h, SelectedSystems= SelectedSystems,
SelectedSystemRoles= SelectedSystemRoles, SelectedSeverities= SelectedSeverities, SelectedRuleTypes= SelectedRuleTypes);
// Query
// get the anomalies
SAPAuditAnomal
// get the user privleged status
| lookup (SAPUsersGetPrivileged() | extend IsPrivilegedUser = true) on
$left.SystemID == $right.SystemID
,$left.User == $right.User
,$left.ClientID == $right.Client
| extend DetailedDescription = iff(IsPrivilegedUser, strcat('User ', User,  ' is a privileged user! <br/>', DetailedDescription), DetailedDescription)
| extend TimeGenerated = MaxTime
// handle threshold per system (configurable via the SAPAuditLogConfigurator watchlist)
//  apply the threshold criteria, consider that the watchlist describes hourly thresholds
| extend MinutesSpan = datetime_diff('Minute', MaxTime, MinTime )
| where EventCount > (Threshold * max_of(MinutesSpan, 10) / 60)
| extend DetailedDescription = iff(EventCount > 1, strcat(' Event occured ', EventCount, ' times within ', MinutesSpan + 1, ' minutes.<br/> ', 'The predefined threshold of ', Threshold,  ' Events per hour was exceeded! <br/>', DetailedDescription), DetailedDescription)
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| top TopN by Score
| project-keep SystemID, Instance, MessageText, MessageClass, MessageID, ClientID, User, TerminalIPv6, TransactionCode, ABAPProgramName, SAPProcesType, SAPWPName, Email, Host, CategoryName, DestinationEmail, DetailedDescription, Tactics, TeamsChannelID, SystemRole, SystemUsage, IsProd, Severity, BagOfDetails, IsPrivilegedUser, EventCount, Threshold, TimeGenerated
| extend Source= 'SAPAuditAnom1 '

// Activation or Deactivation of ICF Service
// Materialize the data for self join to be esier
let TimeAgo= 2h;
let ICFDataChanges = materialize(SAPTableDataLog
| where TimeGenerated > TimeAgo
| where TableName == "ICFSERVLOC"
| where NewValue == "X" or NewValue =="" or isempty( NewValue)// X - Status is activated, "" Not activated now
| where TableField == "ICF_NAME" or TableField == "ICFACTIVE"// Service name// Active status
| project OldValue, NewValue, LogKey, DBLogID, SystemID, TimeGenerated, UserName, Host, Instance);
let ActDeactICF =
ICFDataChanges
| where OldValue == NewValue and OldValue != ""
| lookup ICFDataChanges on
LogKey, DBLogID // Lookup for combining the data
| extend ICFServiceStatus = iff(NewValue1 == "X", "Activated", iff(NewValue1 == "" or isempty( NewValue1) and OldValue1 == "X" ,"Deactivated","No Activity"))
| extend Service = split(LogKey," ")[0];
ActDeactICF
| lookup SAPUsersHeader() on SystemID, $left.UserName== $right.User
| extend AlertText= strcat("Service ", Service, " ", ICFServiceStatus, " by User ", UserName, " in System ", SystemID)//{{Service}} {{ICFServiceStatus}} by User {{User}} in System {{SystemID}}
| project TimeGenerated, Service, PrimaryIP, PrimaryEmail, KnownIPs, KnownEmails, LastKnownIP, User= UserName, Host, SystemID, Client, Instance, ICFServiceStatus, AlertText
| extend Dummy = ''

// Define Variables
// Audit Log Classes - Authorizations for user changed
let Identity = 'IDENTITY';
let ProfileChangeDoc = 'SUSR_UST04';
let Insert = "I";
let logsThreshold = 3600; // 3600 seconds
let AuditClasses = dynamic(['AUB']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveProfiles =  _GetWatchlist('SAP - Sensitive Profiles');
let fixedProfile = datatable(Profile:string)['SAP_ALL','SAP_NEW'];
// Maintain these if System doesn't have CR's
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string)[];
let ChangeDocs =
union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs;
let IdentityChangeDocuments =
// Identity Change documents which represents profiles assignment
    ChangeDocs
    | where ObjectClass == Identity // Identity
    and TableName == ProfileChangeDoc // Profile Change Doc
    and TypeofChange_Item == Insert // Insert
    | extend Profile = ChangedTableKey
    | extend UserAssigned = ObjectID;
let UnitedProfiles =
toscalar(union fixedProfile, SensitiveProfiles
| summarize Profiles = make_list(Profile));
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| summarize by TimeGenerated, TerminalIPv6, User, Host, Email
| lookup kind = leftouter (IdentityChangeDocuments) on User
| where Profile in (UnitedProfiles)
| project-rename  TimeGenAudit = TimeGenerated1
| where abs(datetime_diff('second',TimeGenerated,TimeGenAudit)) <= logsThreshold
or isnull(TimeGenAudit)
| project
// Details
TimeGenerated, Instance , SystemID, ClientID, Profile, User,  UserAssigned,
Email, TerminalIPv6, Host

// Assignment of a sensitive role
// Define Variables
let Roles = 'PFCG';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB', 'AUD']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveRoles =  _GetWatchlist('SAP - Sensitive Roles');
let fixedRoles = datatable(Role: string)['SAP_BC_BASIS_ADMIN', 'SAP_BC_AUTH_PROFILE_ADMIN'];
// here you can exclude system users which are OK to assign sensitive roles
// by adding those users in the SAP_User_Config watchlist with a tag of 'SensitiveRoleOK'
let excludeUsersTags= dynamic(['SensitiveRoleOK']);
let excludedUsers= _GetWatchlist('SAP_User_Config')| where Tags has_any (excludeUsersTags)| summarize by User2Exclude=SAPUser;
let UnitedRoles =
toscalar(union fixedRoles, SensitiveRoles
| summarize Roles = make_list(Role));
let ChangeCheck =
SAPChangeDocsLog
| where ObjectClass == Roles // Roles
    and TableName == UsersRoles // Users Roles
    and TypeofChange_Item == Insert // Insert
| extend UserAssigned = trim_end(@"[^\w]+" ,extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey))
| extend Role = ObjectID
| where Role in (UnitedRoles);
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| summarize by TimeGenerated, TerminalIPv6, User, Host, Email, ClientID, SystemID
| extend User= trim_end(@"[^\w]+" , User)
| lookup kind = inner (ChangeCheck) on User, ClientID, SystemID
| where Role in (UnitedRoles)
| join kind=leftantisemi excludedUsers on $left.User == $right.User2Exclude
| project-rename TimeGenAudit = TimeGenerated1
| where abs(datetime_diff('second', TimeGenerated, TimeGenAudit)) <= logsThreshold
or isnull(TimeGenAudit)
| extend AlertDescription= iff(User == UserAssigned,strcat('User ', User, ' has self-assigned a sensitive role '),strcat('User ', User, ' has Assigned a sensitive role to User ', UserAssigned))
| extend Severity= iff(User == UserAssigned, "High", "Medium")
| extend PackedDetails= pack_all(), Dummy=' '
| lookup (SAPUsersEmail | project SystemID, ClientID, UserAssigned= User, UserAssignedEmail= Email) on UserAssigned, SystemID, ClientID
| extend Tactics= "Privilege Escalation"
| project
    // Details
    TimeGenerated, Instance , SystemID, ClientID, Role, User, UserAssigned,
    Email, TerminalIPv6, Host, AlertDescription, Severity, PackedDetails, UserAssignedEmail, Tactics

// Define variables
// Audit Log Classes - Failed Logons / Password Check
// let AuditClasses = dynamic(['AUO', 'AU2', 'AU6', 'BU1']);
let AuditClasses = dynamic(['AU6']);//RFC only
let perIPLimit = 3;
// Query logic
SAPAuditLog
| where MessageID in (AuditClasses)
| extend DetailsBy = pack("User", User, "Email", Email, "SystemID", SystemID, "ClientID", ClientID)
| summarize LoginbyIPAttempts = count(), Details = make_set(DetailsBy), StartTime = min(TimeGenerated), EndTime = max(TimeGenerated)
    by TerminalIPv6
// Check if number of login attempts per IP is higher than limit
| where LoginbyIPAttempts > perIPLimit
| mv-expand Details
| evaluate bag_unpack(Details, "Details_")
| project
    StartTime, EndTime,  TerminalIPv6,
    Email = column_ifexists("Details_Email", ""), User = column_ifexists("Details_User", ""),
    SysetmID = column_ifexists("Details_SystemID", ""),
   ClientID = column_ifexists("Details_ClientID", "")

// Change in a Sensitive Privileged User
// time span for audit events
let AuditTimeAgo= 75m;
// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'BUV', 'BUW', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'DUH', 'BU2']);
let UsersEmails = SAPUsersHeader()
| project-keep User, SystemID, Client, Email
| project-rename ClientID= Client, ChangedEmail=Email, ChangedUser=User;
SAPAuditLog
| where TimeGenerated > ago(AuditTimeAgo)
| where MessageID in (AuditClasses)
// The user that we are making change in is a sensitive privileged user
| extend ChangedUser = Variable1
| join kind= inner (SAPUsersGetPrivileged())
on $left.ClientID == $right.Client
, $left.SystemID == $right.SystemID
, $left.ChangedUser == $right.User
| extend MessageText = iff(MessageText endswith_cs ".", substring(MessageText, 0, strlen(MessageText)-1), MessageText)
| extend MessageTextDyna = replace_string(MessageText, "User", "Privileged User")
| extend MessageTextDyna = replace_string(MessageTextDyna, "user", "Privileged User")
| lookup UsersEmails
on SystemID, ClientID, ChangedUser
| project-keep TimeGenerated, Instance,  SystemID, ClientID, User, MessageText, MessageID, Email,  TerminalIPv6, Host, ChangedUser, ChangedEmail, MessageTextDyna
| extend Email= iff(isempty(Email),User, Email), Host= iff(isempty(Host), TerminalIPv6, Host), Dummy= ''
| extend ChangedEmail= iff(isempty(ChangedEmail),ChangedUser, ChangedEmail)
| extend PackedDetails= pack_all()

// New Assigned Users
let ObjectClassRoles = 'PFCG';
let TableName = 'CD1251';
let UsersRoles = 'AGR_USERS';
let Insert = "I";
let NotInUse = 'NOT_IN_USE';
let logsThreshold = 3600; // 3600 seconds
// Audit Log Classes - Authorizations for user changed
let AuditClasses = dynamic(['AUB','AUD']); // Authorizations for user &A changed. User Master Record Changed
// Roles Change Documents - Extract Auth Object and Obj Field
let allHistory = ago(0d);
let alertSched = ago(6h); // Please maintain according to schedule
// Maintain these if System doesn't have CR's
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string)[];
let ChangeDocs =
union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs;
let RolesAuthObject = ChangeDocs
    | where TimeGenerated <= allHistory
    | where ObjectClass == ObjectClassRoles and TableName == TableName // Role-Obj-Profile-ObjField
    | where TypeofChange_Item in ('J', 'I', 'U') //  Insert
    | extend RoleObjProfileObjFieldVer = ChangedTableKey, Role = ObjectID
    | extend ObjFieldValue = ValueNew
    | extend ObjField = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 4, RoleObjProfileObjFieldVer, typeof(string)))
    | extend AuthObject = trim(@"\s*?", extract(@"(^.{1,30})\s*?(.{1,10})\s*?(.{1,12})\s*?(.{1,10})\s*?\d{6}", 2, RoleObjProfileObjFieldVer, typeof(string)))
    | summarize by SystemID, Role, AuthObject, ObjField, ObjFieldValue;
let ComplexAuth = _GetWatchlist('SAP - Critical Authorizations');
let SimpleAuth = _GetWatchlist('SAP - Critical Authorizations');
let fixedComplexAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '*',
    'S_DEVELOP', 'OBJTYPE', 'DEBUG', 'ACTVT', '02']; // Maintain these if WatchList is not available
let fixedSimpleAuth = datatable(AuthorizationObject: string, AuthorizationField: string, AuthorizationValue: string, ActivityField: string, Activity: string)
    ['S_TCODE', 'TCD', '*', 'NOT_IN_USE', '',
    'S_TZONE', 'ACTVT', '*', 'NOT_IN_USE', '']; // Maintain these if WatchList is not available
let usersinRole =
    ChangeDocs
    | where TimeGenerated >= alertSched
    | where ObjectClass == ObjectClassRoles // Roles
        and TableName == UsersRoles // Users Roles
        and TypeofChange_Item == Insert // Insert
    | extend UserAssigned = extract(@"^.{1,33}\s*?(.{1,12})\s*?\d{16}", 1, ChangedTableKey)
    | extend Role = ObjectID
    | extend TimeGenUserinRole = TimeGenerated;
    //| summarize by TimeGenerated, SystemID, ClientID, Role, UserAssigned, User
let RolesAuthObjectCheck =
        RolesAuthObject
        | extend ObjFieldVal = ObjFieldValue
        | lookup kind = leftouter
            (RolesAuthObject
            | extend ActivityVal = ObjFieldValue)
            on Role, AuthObject;
let complexScenario =
    union ComplexAuth, fixedComplexAuth
    | where ActivityField != NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObjectCheck)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue
        and $left.ActivityField == $right.ObjField1
        and $left.Activity == $right.ActivityVal;
let simpleScenario =
    union SimpleAuth, fixedSimpleAuth
    | where ActivityField == NotInUse
    | summarize by AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity
    | lookup kind = inner (RolesAuthObject)
        on $left.AuthorizationObject == $right.AuthObject
        and $left.AuthorizationField == $right.ObjField
        and $left.AuthorizationValue == $right.ObjFieldValue;
let GetEntites =
    SAPAuditLog
    | where TimeGenerated >= alertSched
    | where MessageID in (AuditClasses)
    | summarize by TimeGenerated, TerminalIPv6, ClientID, User, Host, Email
    | extend TimeGenAudit = TimeGenerated;
union complexScenario, simpleScenario
| lookup kind = inner (usersinRole) on SystemID, Role
| lookup kind = leftouter (GetEntites) on User
| where abs(datetime_diff('second', TimeGenUserinRole, TimeGenAudit)) <= logsThreshold or
isnull(TimeGenAudit)
| project
    // Details
TimeGenUserinRole , SystemID, ClientID, Role, User, UserAssigned, AuthorizationObject, AuthorizationField, AuthorizationValue, ActivityField, Activity, Email, TerminalIPv6, Host

