let Configuration = materialize (_GetWatchlist('SAP_Dynamic_Audit_Log_Monitor_Configuration') 
    | summarize arg_max(LastUpdatedTimeUTC, *) by SearchKey
    | where (SelectedSeverities has ProductionSeverity or SelectedSeverities has NonProdSeverity or tostring(SelectedSeverities) == '["All Severities"]')
    | where (SelectedRuleTypes has RuleType or tostring(SelectedRuleTypes) == '["All RuleTypes"]')
    | extend DummyJoinField = 1);

let Systems = materialize (_GetWatchlist('SAP - Systems') 
    | where SearchKey in (SelectedSystems) or tostring(SelectedSystems) == '["All Systems"]'
    | where SystemRole in (SelectedSystemRoles) or tostring(SelectedSystemRoles) == '["All System Roles"]'
    | project-keep SearchKey, SystemRole, SystemUsage
    | extend DummyJoinField = 1);


let SystemConfigurations =  Configuration
    | join kind= inner Systems
        on $left.DummyJoinField == $right.DummyJoinField;


SystemConfigurations
| project-rename SystemID = SearchKey1
// | extend IsProd = iff((SystemRole == "Production" or ConsiderAsProd has SystemID) and not(ConsiderAsNonProd has SystemID), true, false)
| extend IsProd = iff(SystemRole == "Production" , true, false)
| extend Severity = iff(IsProd, ProductionSeverity, NonProdSeverity)
| extend Threshold = tolong( iff(IsProd, ProductionThreshold, NonProdThreshold))
| where (SelectedSeverities has Severity or  tostring(SelectedSeverities) == '["All Severities"]')
