User,
Email = SMTP_ADDR,
UserType = USTYP,
Timezone = TZONE,
LockedStatus = UFLAG,
LastSeen,
LastSeenDaysAgo,
PrimaryIP,
LastKnownIP,
PrimaryEmail,
KnownIPs,
