// function parameters:
//// solution version check has moved to be handled by the "Content hub"
//// let LatestSolutionVersion = '1.2.95';
//// let ThisSolutionVersion= '2.3.00';// the version number is being refreshed during solution packaging
//// let LatestSolutionDateTime = todatetime(LatestSolutionDate);
// let RedAgo = 31d; 
// let YellowAgo = 1d;
// let GreenAgo= 2h;
// let LatestAgentVersion = '1.1.1';

// colors are there for the data connector UI
let colors = dynamic (['Green', 'Yellow', 'Red', 'Header', 'Unclear', 'Footer']);
// prepare a template structure in case we are  missing the SAP_HeartBeat_CL table
let AgentHealthEmpty= datatable (Agent: string, SystemID: string, LastHealthSeen: datetime, LastDataSeen: datetime, TimeGenerated: datetime, LastConfigedOn: datetime, agent_id_s: string, AgentGUID: string, AgentName: string, KeyMode: string, SdkPath: string, VaultId: string, agent_ver_s: string     
) ['Sentinel solution', 'Solution version check', '2021-03-01T00:00:00Z', '2021-03-01T00:00:00Z', '2021-03-01T00:00:00Z', '2021-03-01T00:00:00Z','', '', '', '', '', '', ''];

// get the configuration of the agents created by the data connector UI
let SAPAgentDetailsWL= (_GetWatchlist('SAPAgentDetails')| project-rename agent_id_s= SearchKey, LastConfigedOn= LastUpdatedTimeUTC 
| extend system_id_s = 'Configuration check', AgentGUID= tostring(column_ifexists('AgentGUID','')));

// combine the heartbeats and the watchlist data to produce a list of agents and system IDs. 
let AgentHealth = materialize((union isfuzzy=true AgentHealthEmpty, SAPAgentDetailsWL, SAP_HeartBeat_CL)
        | where TimeGenerated > ago(RedAgo) or isnull(TimeGenerated)
        | extend LastHealthSeen = max_of(TimeGenerated, LastHealthSeen)
        // add agents from the SAPAgentDetails watchlist in case these are not yet sending data
        | summarize arg_max(LastHealthSeen, *) by agent_id_s, system_id_s
        | extend Agent= agent_id_s
            , SystemID = iff(system_id_s== 'Configuration check', 'Configuration check', substring(system_id_s, 0, 3))
            , AgentVersion = agent_ver_s);

// get the version per agent
let AgentsHeader = AgentHealth | summarize arg_max(LastHealthSeen, Agent, AgentVersion)
| project-keep Agent, AgentVersion, LastHealthSeen
| extend SystemID = 'Agent Version Check';


// prepare a template structure in case we are  missing all ABAP* tables
let AgentPotentialEmpty = datatable (PotentialAgent: string, SystemID_s: string, TimeGenerated: date)[];

// agent potential- we are looking for SAP systems that send data but not heartbeats. a special case of very old agents that did not have heartbeats
let AgentPotential = AgentPotentialEmpty
    | union isfuzzy=true  ABAP* 
    | where TimeGenerated > ago(RedAgo) and isnotempty(SystemID_s) 
    | summarize arg_max(TimeGenerated, SystemID_s) by SystemID_s
    | project SystemID_s, TimeGenerated
    | extend PotentialAgent = strcat(SystemID_s, ' ')
    | project-rename SystemID = SystemID_s
        , LastDataSeen = TimeGenerated;

// helper function- handle values and texts for time spans
let FtimePassed = (DateTime: datetime, Occured: int = 1) {
    case(
    datetime_diff('Hour', now(), DateTime) < 0 or isnull(DateTime), bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), 'is missing', strcat('{TimeScale', tostring(Occured), '}'), '')
    , datetime_diff('Hour', now(), DateTime) < 2, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), '1', strcat('{TimeScale', tostring(Occured), '}'), 'hour')
    , datetime_diff('Hour', now(), DateTime) < 24, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), tostring(datetime_diff('Hour', now(), DateTime)), strcat('{TimeScale', tostring(Occured), '}'), 'hours') 
    , datetime_diff('Hour', now(), DateTime) < 48, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), '1', strcat('{TimeScale', tostring(Occured), '}'), 'day')
    , bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), tostring(datetime_diff('Day', now(), DateTime)), strcat('{TimeScale', tostring(Occured), '}'), 'days'))};

// helper function- determine statuses and details per SID
let FBaggedDetails = (Agent: string, LastDataSeen: datetime, LastHealthSeen: datetime, LastConfigedOn: datetime, LastHealthSeen4Agent: datetime ,GreenAgo: timespan, YellowAgo: timespan, RedAgo: timespan,  AgentVersion: string='0.0', SystemID: string='') {
    case(

// helper function- to make judgement on the agent/ SID status/ version
// agent version checks:
// UPAVLA - An upgrade is available for the Microsoft Sentinel for SAP Agent. <br> Current Agent released date {CurrentVersion}, latest version relesed on {LatestVersion}.
 (SystemID == 'Agent Version Check' and strcmp(LatestAgentVersion, AgentVersion) > 0), bag_pack('Details', 'Agent update available', 'ExtendedDetails', 'An upgrade is available for the Microsoft Sentinel for SAP Agent', 'Status', 'Header', 'MessageCode', 'UPAVLA', 'Variables', bag_pack('{CurrentVersion}', AgentVersion, '{LatestVersion}', LatestAgentVersion)) 

// UPNAVLA - The current agent's version is the latest available. <br>Current Version release data {CurrentVersion}.
, SystemID == 'Agent Version Check', bag_pack('Details', 'Current agent version is latest available','ExtendedDetails', 'The current agent version is the latest available', 'Status', 'Remove', 'MessageCode', 'UPNAVLA', 'Variables', bag_pack('{CurrentVersion}', AgentVersion)) 

// Agent Configuration checks:
// ACIOK - Agent configuration is there (since GreenAgo) and so is heartbeat, remove this configuration check (Remove)
, (isnotempty(LastConfigedOn) and (max_of(LastHealthSeen4Agent, LastConfigedOn) > ago(GreenAgo))), bag_pack('Details', 'Agent configuration is there','ExtendedDetails', strcat('Agent was configured ', FtimePassed(LastConfigedOn)['{TimeSpan1}'],' ', FtimePassed(LastConfigedOn)['{TimeScale1}'], ' ago, and heartbeat sent since then'), 'Status', 'Remove', 'MessageCode', 'ACIOK', 'Variables', FtimePassed(LastConfigedOn))

// ACCUY - Agent configuration is there, and there is no heartbeat since (between green and yellow).- (Yellow, StatusCode= 1, )
, ((isnotempty(LastConfigedOn) and (max_of(LastHealthSeen4Agent, LastConfigedOn) > ago(YellowAgo)))), bag_pack('Details', 'Agent configuration is incomplete','ExtendedDetails', strcat('Agent was configured ', FtimePassed(LastConfigedOn)['{TimeSpan1}'],' ', FtimePassed(LastConfigedOn)['{TimeScale1}'], ' ago, but no heartbeat was recieved since then. Please review configuration'), 'Status', 'Yellow', 'MessageCode', 'ACCUY', 'Variables', FtimePassed(LastConfigedOn))

// ACCOY - Agent configuration is there, and there is no heartbeat since (between yellow and red).- (Red, StatusCode=2, )
, (isnotempty(LastConfigedOn) and max_of(LastHealthSeen4Agent, LastConfigedOn) < ago(YellowAgo)), bag_pack('Details', 'Agent configuration is incomplete','ExtendedDetails', strcat('Agent was configured ', FtimePassed(LastConfigedOn)['{TimeSpan1}'],' ', FtimePassed(LastConfigedOn)['{TimeScale1}'], ' ago, but no heartbeat was recieved since then. Please review configuration'), 'Status', 'Red', 'MessageCode', 'ACCOY', 'Variables', FtimePassed(LastConfigedOn))

// handle missing data or heartbeat at the systems ID level:
// OKOK - Data or heartbeat recieved in the last {TimeSpan1}' ' {TimeScale1}.
, (max_of(LastHealthSeen, LastDataSeen) > ago(GreenAgo) and isnotnull(LastHealthSeen)), bag_pack('Details', 'System healthy','ExtendedDetails', strcat('Data or heartbeat recieved in the last ', FtimePassed(ago(GreenAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(GreenAgo))['{TimeScale1}']), 'Status', 'Green', 'MessageCode', 'OKOK', 'Variables', FtimePassed(ago(GreenAgo)))

// MHOY - Missing hearbeat for over {TimeSpan1}' ' {TimeScale1} days, but data is OK. Please consider to upgrade the connector.
, (LastHealthSeen < ago(RedAgo) or isnull(LastHealthSeen)) and LastDataSeen > ago(GreenAgo), bag_pack('Details', 'Connector update available','ExtendedDetails', strcat('Missing hearbeat for over ', FtimePassed(ago(RedAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(RedAgo))['{TimeScale1}'], ', but data is OK. Please consider to upgrade the connector'), 'Status', 'Yellow', 'MessageCode', 'MHOY', 'Variables', FtimePassed(ago(RedAgo)))

// MDOG - missing data and hearbeat for over GreenAgo timespan - Missing data and heartbeat for over {TimeSpan1}' ' {TimeScale1}.  Check Connectivity.
, (max_of(LastHealthSeen, LastDataSeen) > ago(YellowAgo)), bag_pack('Details', 'System unreachable for over 2 hours','ExtendedDetails', strcat('Missing data and heartbeat for over ', FtimePassed(ago(GreenAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(GreenAgo))['{TimeScale1}']), 'Status', 'Yellow', 'MessageCode', 'MDOG', 'Variables', FtimePassed(ago(GreenAgo))) 

// MDOY - missing data and heartbeat for over YellowAgo timespan - Missing data and heartbeat for over {TimeSpan1}' ' {TimeScale1}.  Check Connectivity.
, (max_of(LastHealthSeen, LastDataSeen) > ago(RedAgo)), bag_pack('Details', 'System unreachable for over 24 hours','ExtendedDetails', strcat('Missing data and heartbeat for over ', FtimePassed(ago(YellowAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(YellowAgo))['{TimeScale1}']), 'Status', 'Red', 'MessageCode', 'MDOY', 'Variables', FtimePassed(ago(YellowAgo))) 

// UNCLEAR - fallback of all cases, 'Data Last Recieved %s ago, heartbeat last recieved %s ago' {Variable1, Variable2}
, bag_pack('Details', 'Status unclear – check system','ExtendedDetails', strcat('Data last recieved ', FtimePassed(LastDataSeen)['{TimeSpan1}'], ' ', FtimePassed(LastDataSeen)['{TimeScale1}'], ' ago, heartbeat last recieved ', FtimePassed(LastHealthSeen, 2)['{TimeSpan2}'], ' ', FtimePassed(LastHealthSeen, 2)['{TimeScale2}'], ' ago'), 'Status', 'Unclear', 'MessageCode', 'UNCLEAR', 'Variables', bag_merge(FtimePassed(LastDataSeen), FtimePassed(DateTime= LastHealthSeen, Occured=2))))
};

// gather the heartbeat and configuration and add a line per agent for version check
AgentHealth | union AgentsHeader
// now join again with agentHeader to get the last heartbeat recieved from the agent 
| join kind= leftouter (AgentsHeader | project Agent, LastHealthSeen4Agent= LastHealthSeen) on Agent
// SID health from inferred from data coming in
| join kind=fullouter AgentPotential on $left.SystemID == $right.SystemID
| extend Agent = iff(isempty(Agent), PotentialAgent, Agent), LastSeen = max_of(LastHealthSeen, LastDataSeen)
| extend SystemID = iff(SystemID == SystemID1, SystemID1, strcat(SystemID1, SystemID))
// get the judgement from FBaggedDetails
| extend BaggedDetails = FBaggedDetails(Agent, LastDataSeen, LastHealthSeen, LastConfigedOn, LastHealthSeen4Agent, GreenAgo, YellowAgo, RedAgo, AgentVersion, SystemID)
| evaluate bag_unpack(BaggedDetails)
// not everything is interesting
| where Status != 'Remove'
| where Agent != 'Sentinel solution' // solution version check is deprecated
// convert the status into codes
| extend StatusCode = array_index_of(colors, Status)
| where isnotempty(SystemID)
| project-reorder Agent, SystemID, MessageCode, Details, ExtendedDetails, LastSeen, Status, StatusCode, Variables, AgentGUID, AgentName, KeyMode, SdkPath, VaultId
| project-keep Agent, SystemID, MessageCode, Details, ExtendedDetails, LastSeen, Status, StatusCode, Variables, AgentGUID, AgentName, KeyMode, SdkPath, VaultId
| order by StatusCode desc

