let ThisSolutionVersion= '1.3.00';// the version number is being refreshed during solution packaging

// let LatestSolutionDateTime = todatetime(LatestSolutionDate);
let colors = dynamic (['Green', 'Yellow', 'Red', 'Header', 'Unclear', 'Footer']);
