    SystemID: string,
    LastHealthSeen: datetime,
    agent_id_s: string 
) [
    'Sentinel solution', 'Solution version check', '2021-03-01T00:00:00Z',''
];

let AgentHealth = AgentHealthEmpty
    | union isfuzzy=true kind=outer (SAP_HeartBeat_CL 
        | where TimeGenerated > ago(RedAgo) or isnull(TimeGenerated)
        | project-rename LastHealthSeen = TimeGenerated
        // | summarize arg_max(LastHealthSeen, agent_id_s, system_id_s, agent_ver_s) by system_id_s, agent_id_s
        | summarize arg_max(LastHealthSeen, agent_id_s, system_id_s, agent_ver_s) by system_id_s
        | project Agent= agent_id_s
            , SystemID = substring(system_id_s, 0, 3)
            , LastHealthSeen
            // , agent_id_s
            , AgentVersion = agent_ver_s);

let AgentsVersion = AgentHealth | summarize arg_max(LastHealthSeen, Agent, AgentVersion)
| project-keep Agent, AgentVersion, LastHealthSeen
