// AgentsVersion


// prepare a template structure in case we are  missing all ABAP* tables
let AgentPotentialEmpty = datatable (PotentialAgent: string, SystemID_s: string, TimeGenerated: date)[];

let AgentPotential = AgentPotentialEmpty
    | union isfuzzy=true  ABAP* 
    | where TimeGenerated > ago(RedAgo) and isnotempty(SystemID_s) 
    | summarize arg_max(TimeGenerated, SystemID_s) by SystemID_s
    | project SystemID_s, TimeGenerated
    | extend PotentialAgent = strcat(SystemID_s, ' ')
    | project-rename SystemID = SystemID_s
        , LastDataSeen = TimeGenerated;

