// let Is1stHigher = (This:string , Other:string ){todouble(This) > todouble(Other)};

// handle values and texts for time spans
let FtimePassed = (DateTime: datetime, Occured: int = 1) {
    case(
    datetime_diff('Hour', now(), DateTime) < 0 or isnull(DateTime), bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), 'is missing', strcat('{TimeScale', tostring(Occured), '}'), '')
    , datetime_diff('Hour', now(), DateTime) < 2, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), '1', strcat('{TimeScale', tostring(Occured), '}'), 'hour')
    , datetime_diff('Hour', now(), DateTime) < 24, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), tostring(datetime_diff('Hour', now(), DateTime)), strcat('{TimeScale', tostring(Occured), '}'), 'hours') 
    , datetime_diff('Hour', now(), DateTime) < 48, bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), '1', strcat('{TimeScale', tostring(Occured), '}'), 'day')
    , bag_pack(strcat('{TimeSpan', tostring(Occured), '}'), tostring(datetime_diff('Day', now(), DateTime)), strcat('{TimeScale', tostring(Occured), '}'), 'days'))     
};

//determine statuses and details per SID
let FBaggedDetails = (Agent: string, LastDataSeen: datetime, LastHealthSeen: datetime, GreenAgo: timespan, YellowAgo: timespan, RedAgo: timespan,  AgentVersion: string='0.0', SystemID: string='') {
    case(
    // UPAVLS - An upgrade is available for the Microsoft Sentinel for SAP solution. <br> Current solution version {CurrentVersion}, latest version {LatestVersion}.
    (Agent == 'Sentinel solution' and strcmp(LatestSolutionVersion,ThisSolutionVersion) >0), bag_pack('Details', 'Solution update available','ExtendedDetails', 'An upgrade is available for the Microsoft Sentinel for SAP solution', 'Status', 'Header', 'MessageCode', 'UPAVLS', 'Variables', bag_pack('{CurrentVersion}', ThisSolutionVersion, '{LatestVersion}', LatestSolutionVersion))

    // UPAVLA - An upgrade is available for the Microsoft Sentinel for SAP Agent. <br> Current Agent released date {CurrentVersion}, latest version relesed on {LatestVersion}.
, (SystemID == 'Agent Version Check' and strcmp(LatestAgentVersion, AgentVersion) > 0), bag_pack('Details', 'Agent update available', 'ExtendedDetails', 'An upgrade is available for the Microsoft Sentinel for SAP Agent', 'Status', 'Header', 'MessageCode', 'UPAVLA', 'Variables', bag_pack('{CurrentVersion}', AgentVersion, '{LatestVersion}', LatestAgentVersion)) 

    // UPNAVLS - The current solution's version is the latest available. <br>Current Version release data {CurrentVersion}.
, Agent == 'Sentinel solution', bag_pack('Details', 'Current solution version is latest available','ExtendedDetails', 'The current solution version is the latest available', 'Status', 'Remove', 'MessageCode', 'UPNAVLS', 'Variables', bag_pack('{CurrentVersion}', ThisSolutionVersion)) 

    // UPNAVLA - The current agent's version is the latest available. <br>Current Version release data {CurrentVersion}.
, SystemID == 'Agent Version Check', bag_pack('Details', 'Current agent version is latest available','ExtendedDetails', 'The current agent version is the latest available', 'Status', 'Remove', 'MessageCode', 'UPNAVLA', 'Variables', bag_pack('{CurrentVersion}', AgentVersion)) 

    // OKOK - Data or heartbeat recieved in the last {TimeSpan1}' ' {TimeScale1}.
, (max_of(LastHealthSeen, LastDataSeen) > ago(GreenAgo) and isnotnull(LastHealthSeen)), bag_pack('Details', 'System healthy','ExtendedDetails', strcat('Data or heartbeat recieved in the last ', FtimePassed(ago(GreenAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(GreenAgo))['{TimeScale1}']), 'Status', 'Green', 'MessageCode', 'OKOK', 'Variables', FtimePassed(ago(GreenAgo)))

    // MHOY - Missing hearbeat for over {TimeSpan1}' ' {TimeScale1} days, but data is OK. Please consider to upgrade the connector.
, (LastHealthSeen < ago(RedAgo) or isnull(LastHealthSeen)) and LastDataSeen > ago(GreenAgo), bag_pack('Details', 'Connector update available','ExtendedDetails', strcat('Missing hearbeat for over ', FtimePassed(ago(RedAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(RedAgo))['{TimeScale1}'], ', but data is OK. Please consider to upgrade the connector'), 'Status', 'Yellow', 'MessageCode', 'MHOY', 'Variables', FtimePassed(ago(RedAgo)))

    // MDOG - missing data and hearbeat for over GreenAgo timespan - Missing data and heartbeat for over {TimeSpan1}' ' {TimeScale1}.  Check Connectivity.
, (max_of(LastHealthSeen, LastDataSeen) > ago(YellowAgo)), bag_pack('Details', 'System unreachable for over 2 hours','ExtendedDetails', strcat('Missing data and heartbeat for over ', FtimePassed(ago(GreenAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(GreenAgo))['{TimeScale1}']), 'Status', 'Yellow', 'MessageCode', 'MDOG', 'Variables', FtimePassed(ago(GreenAgo))) 

    // MDOY - missing data and heartbeat for over YellowAgo timespan - Missing data and heartbeat for over {TimeSpan1}' ' {TimeScale1}.  Check Connectivity.
, (max_of(LastHealthSeen, LastDataSeen) > ago(RedAgo)), bag_pack('Details', 'System unreachable for over 24 hours','ExtendedDetails', strcat('Missing data and heartbeat for over ', FtimePassed(ago(YellowAgo))['{TimeSpan1}'], ' ' , FtimePassed(ago(YellowAgo))['{TimeScale1}']), 'Status', 'Red', 'MessageCode', 'MDOY', 'Variables', FtimePassed(ago(YellowAgo))) 

    // UNCLEAR - fallback of all cases, 'Data Last Recieved %s ago, heartbeat last recieved %s ago' {Variable1, Variable2}
, bag_pack('Details', 'Status unclear – check system','ExtendedDetails', strcat('Data last recieved ', FtimePassed(LastDataSeen)['{TimeSpan1}'], ' ', FtimePassed(LastDataSeen)['{TimeScale1}'], ' ago, heartbeat last recieved ', FtimePassed(LastHealthSeen, 2)['{TimeSpan2}'], ' ', FtimePassed(LastHealthSeen, 2)['{TimeScale2}'], ' ago'), 'Status', 'Unclear', 'MessageCode', 'UNCLEAR', 'Variables', bag_merge(FtimePassed(LastDataSeen), FtimePassed(DateTime= LastHealthSeen, Occured=2))))
};

// agent & SID health from the SAP_HeartBeat_CL table
AgentHealth | union AgentsVersion
// SID health from inferred from data coming in
| join kind=fullouter AgentPotential
    on
    $left.SystemID == $right.SystemID
| extend Agent = iff(isempty(Agent), PotentialAgent, Agent)
    , LastSeen = max_of(LastHealthSeen, LastDataSeen)
| extend SystemID = iff(SystemID == SystemID1, SystemID1, strcat(SystemID1, SystemID))
| extend BaggedDetails = FBaggedDetails(Agent, LastDataSeen, LastHealthSeen, GreenAgo, YellowAgo, RedAgo, AgentVersion, SystemID)
| evaluate bag_unpack(BaggedDetails)
| where Status != 'Remove'
| extend StatusCode = array_index_of(colors, Status)
// | extend ExtendedDetails = Details
| project-keep
    Agent,
    SystemID,
    MessageCode,
    Details,
    ExtendedDetails,
    LastSeen,
    Status,
    StatusCode,
    Variables
| project-reorder
    Agent,
    SystemID,
    MessageCode,
    Details,
    ExtendedDetails,
    LastSeen,
    Status,
    StatusCode,
    Variables
| where isnotempty(SystemID)
// | union isfuzzy=true TestData
| order by StatusCode desc 
| limit 50

