body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "New incident from Azure Sentinel!"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "width": "auto",
                            "style": "attention",
                            "items": [
                                {
                                    "type": "RichTextBlock",
                                    "inlines": [
                                        {
                                            "type": "TextRun",
                                            "text": "@{triggerBody()?['object']?['properties']?['severity']}"
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "spacing": "Medium",
                    "size": "Medium",
                    "weight": "Bolder",
                    "color": "Accent",
                    "text": "@{triggerBody()?['object']?['properties']?['title']}"
                },
                {
                    "type": "ActionSet",
                    "actions": [
                        {
                            "type": "Action.OpenUrl",
                            "title": "View incident",
                            "url": "@{triggerBody()?['object']?['properties']?['incidentUrl']}",
                            "id": "openSentinel"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Creation time (UTC)",
                            "value": "@{triggerBody()?['object']?['properties']?['createdTimeUtc']}"
                        },
                        {
                            "title": "Tactics",
                            "value": "@{variables('Sentinel-Tactics')}"
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "text": "@{replace(replace(triggerBody()?['object']?['properties']?['description'],'"','\"'),'\\','\')}",
                    "wrap": true
                }
            ]
        },
        {
            "type": "Container",
            "spacing": "Large",
            "style": "emphasis",
            "items": [
                {
                    "type": "TextBlock",
                    "text": "Related Entities",
                    "wrap": true,
                    "separator": true,
                    "fontType": "Default",
                    "size": "Medium",
                    "weight": "Bolder"
                },
                {
                    "type": "ColumnSet",
                    "columns": @{outputs('Compose_dynamic_json_for_entities_table')}
                }
            ],
            "bleed": true
        }
    ],
    "actions": [
        {
            "type": "Action.ShowCard",
            "title": "Block User on...",
            "style": "destructive",
            "card": {
                "type": "AdaptiveCard",
                "body": [
                    {
                        "type": "Input.ChoiceSet",
                        "id": "WhereToLockUser",
                        "label": "On which system?",
                        "style": "expanded",
                        "choices": [
                            {
                                "title": "SAP ERP",
                                "value": "erp"
                            },
                            {
                                "title": "SAP BTP Cloud Identity Service",
                                "value": "btp"
                            },
                            {
                                "title": "Azure AD (globally)",
                                "value": "aad"
                            }
                        ],
                        "value": "erp",
                        "isRequired": true,
                        "errorMessage": "Given choice unknown!"
                    },
                    {
                        "type": "Input.Text",
                        "id": "blockComment",
                        "placeholder": "Add a comment",
                        "isMultiline": true
                    }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "id": "block",
                        "title": "Submit"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.4"
            }
        },
        {
            "type": "Action.ShowCard",
            "title": "Flag as false positive",
            "card": {
                "type": "AdaptiveCard",
                "body": [
                    {
                        "type": "Input.Text",
                        "id": "flagComment",
                        "placeholder": "Add a comment",
                        "isMultiline": true
                    }
                ],
                "actions": [
                    {
                        "type": "Action.Submit",
                        "id": "flag",
                        "title": "Submit"
                    }
                ],
                "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
                "version": "1.4"
            }
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "fallbackText": "This card requires Adaptive Cards v1.4 support to be rendered properly."
}
