body.messageBody:
{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "Container",
            "style": "emphasis",
            "items": [
                {
                    "type": "ColumnSet",
                    "columns": [
                        {
                            "type": "Column",
                            "items": [
                                {
                                    "type": "TextBlock",
                                    "size": "Large",
                                    "weight": "Bolder",
                                    "text": "New incident from Azure Sentinel!"
                                }
                            ],
                            "width": "stretch"
                        },
                        {
                            "type": "Column",
                            "width": "auto",
                            "style": "attention",
                            "items": [
                                {
                                    "type": "RichTextBlock",
                                    "inlines": [
                                        {
                                            "type": "TextRun",
                                            "text": "@{triggerBody()?['object']?['properties']?['severity']}"
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "spacing": "Medium",
                    "size": "Medium",
                    "weight": "Bolder",
                    "color": "Accent",
                    "text": "@{triggerBody()?['object']?['properties']?['title']}"
                },
                {
                    "type": "ActionSet",
                    "actions": [
                        {
                            "type": "Action.OpenUrl",
                            "title": "View incident",
                            "url": "@{triggerBody()?['object']?['properties']?['incidentUrl']}",
                            "id": "openSentinel"
                        }
                    ]
                }
            ],
            "bleed": true
        },
        {
            "type": "Container",
            "items": [
                {
                    "type": "FactSet",
                    "spacing": "Large",
                    "facts": [
                        {
                            "title": "Creation time (UTC)",
                            "value": "@{triggerBody()?['object']?['properties']?['createdTimeUtc']}"
                        },
                        {
                            "title": "Tactics",
                            "value": "@{triggerBody()?['object']?['properties']?['additionalData']?['tactics'][0]}"
                        },
                        {
                            "title": "⚠️Corrective Action",
                            "value": "SAP Audit Log on  @{variables('SID')} has been automatically re-activated. This is a critical event! You need to investigate the root cause."
                        }
                    ]
                },
                {
                    "type": "TextBlock",
                    "text": "@{replace(replace(triggerBody()?['object']?['properties']?['description'],'"','\"'),'\\','\')}",
                    "wrap": true
                }
            ]
        },
        {
            "type": "Container",
            "spacing": "Large",
            "style": "emphasis",
            "items": [
                {
                    "type": "TextBlock",
                    "text": "Related Entities",
                    "wrap": true,
                    "separator": true,
                    "fontType": "Default",
                    "size": "Medium",
                    "weight": "Bolder"
                },
                {
                    "type": "ColumnSet",
                    "columns": @{outputs('Compose_dynamic_json_for_entities_table')}
                }
            ],
            "bleed": true
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "fallbackText": "This card requires Adaptive Cards v1.4 support to be rendered properly."
}
