let PrivUsers = SAPUsersGetPrivileged | summarize by User;
SAPAuditLog
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where User in (PrivUsers)
| summarize count() by bin(TimeGenerated,1h), User

// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AUD']);

let PrivUsers = SAPUsersGetPrivileged | summarize by User;
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where MessageID in (AuditClasses)
| where User in (PrivUsers) // The user that makes a change is a sensitive privileged user
| summarize count() by User

// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'BUV', 'BUW', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'DUH', 'BU2']);
// Get Relevant User from WatchList

let PrivUsers = SAPUsersGetPrivileged | summarize by User;
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where MessageID in (AuditClasses)
| where Variable1 in (PrivUsers) // The user that we are making change in is a sensitive privileged user
| summarize by Variable1

// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AUD']);
// Get Relevant User from WatchList
let PrivelegedUsers = _GetWatchlist('SAP - Privileged Users');
let fixedUsers = datatable(User: string)
    // Maintain these if WatchList is not available    
    ["SAP*", "DDIC"]
;
let UnitedPrivleged = union PrivelegedUsers, fixedUsers
| summarize by User;
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where MessageID in (AuditClasses)
| lookup kind=inner SAPChangeDocsLog on User
| where TypeofChange_Header == "U"
| where User == '{UserChange2}'
| project TimeGenerated ,SystemID, ClientID, UserChanged = Variable1,Table = TableName, ObjectClass, ChangedTableKey, Field = FieldName, ObjectID , OldValue = ValueOld, NewValue = ValueNew

// Audit Log Classes - User Master Changes
let AuditClasses = dynamic(['AU7', 'BUV', 'BUW', 'AU8', 'AU9', 'AUA', 'AUB', 'AUD', 'DUH', 'BU2']);
// Get Relevant User from WatchList
let PrivelegedUsers = _GetWatchlist('SAP - Privileged Users');
let fixedUsers = datatable(User: string)
    // Maintain these if WatchList is not available    
    ["SAP*", "DDIC"]
;
let SensProfiles = _GetWatchlist('SAP - Sensitive Profiles') | summarize  by Profile;
let SensRoles = _GetWatchlist('SAP - Sensitive Roles') | summarize  by Role;
// Maintain these if System doesn't have CR's
let FixedUserMD = datatable(TimeGenerated : datetime, UserName_s : string, RoleMembership_s : string)[];
// Priveleged Users by sensitive role/profile based on user master data
let ABAPUserMd = 
union isfuzzy=true table("ABAPUserMD_CL"), FixedUserMD;
let PrivUserdByMD = ABAPUserMd
| extend Roles = parse_json(RoleMembership_s)["SapRole"], Profiles = parse_json(RoleMembership_s)["SapProfile"]
| mv-expand Roles, Profiles
| where Roles in (SensRoles) or Profiles in (SensProfiles)
| summarize by User = UserName_s;
let UnitedPrivleged = union PrivelegedUsers, fixedUsers, PrivUserdByMD
| summarize by User;
SAPAuditLog 
| where MessageID in (AuditClasses)
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where Variable1 == '{ChangedUser}'
| lookup kind=inner SAPChangeDocsLog on User
| where TypeofChange_Header == "U"
| project TimeGenerated ,SystemID, ClientID, UserAction = User, Table = TableName, ObjectClass, ChangedTableKey, Field = FieldName, ObjectID , OldValue = ValueOld, NewValue = ValueNew

// Define variables
let ChangeAndLoginThreshold = 5; // Difference by minutes between Change in user to connect
let LoginAndRestThreshold = 3; // Difference by minutes between Connect and password reset
let AuditUserChange = dynamic(['AUD']); // Message of change in user
let AuditLogIn = dynamic(['AU1', 'AU5']); // Messages of connect with user
let AuditUserPassReset = dynamic(['BU2']); // Message of user password change

let UnitedPrivleged = SAPUsersGetPrivileged | summarize by User;
// Query Logic
let UserChange = // Get users change activity
SAPAuditLog
| where TimeGenerated {TimeSelect}
| where MessageID in (AuditUserChange)
| where Variable1 in (UnitedPrivleged) // The user with the changed password is privileged user
| project-rename UserChangeTimeGenerated = TimeGenerated, UserPassChanged = Variable1;
let UserLogIn = // Get users connections
SAPAuditLog
| where TimeGenerated {TimeSelect}
| where MessageID in (AuditLogIn)
| project-rename UserLogInTimeGenerated = TimeGenerated;
SAPAuditLog // Get users password reset
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where MessageID in (AuditUserPassReset)
| where User in (UnitedPrivleged) // The user with the changed password is privileged user
| lookup kind=inner UserLogIn on TerminalIPv6, User // Lookup of user reset to user login
| lookup kind=inner UserChange on TerminalIPv6, $left.User == $right.UserPassChanged // Lookup of user reset to user change
| where abs(datetime_diff('Minute', UserChangeTimeGenerated, UserLogInTimeGenerated)) <= ChangeAndLoginThreshold // Connect after a change in user
| where abs(datetime_diff('Minute', TimeGenerated, UserLogInTimeGenerated)) <= LoginAndRestThreshold // Change password after connection
// | summarize by TimeGenerated, SystemID, ClientID, UserAction = User1, PrivUserPassChanged = User, AccountCustomEntity = Email, IPCustomEntity = TerminalIPv6, HostCustomEntity = Host
| summarize by User

// Define Variables
// Audit Log Classes - Authorizations for user changed
let Identity = 'IDENTITY';
let ProfileChangeDoc = 'SUSR_UST04';
let Insert = "I";
let logsThreshold = 10; // 10 seconds
let AuditClasses = dynamic(['AUB']); // Authorizations for user &A changed.
// Maintain these if WatchList is not available
let SensitiveProfiles =  _GetWatchlist('SAP - Sensitive Profiles');
let fixedProfile = datatable(Profile:string)['SAP_ALL','SAP_NEW'];
// Maintain these if System doesn't have CR's
let fixedChangeDocs = datatable(User : string, ObjectClass : string, TableName : string, TypeofChange_Item : string , ChangedTableKey : string, ObjectID : string, TimeGenerated : datetime, ValueNew : string, SystemID : string)[];
let ChangeDocs = 
union isfuzzy=true table("SAPChangeDocsLog"), fixedChangeDocs;
let IdentityChangeDocuments =
// Identity Change documents which represents profiles assignment
    ChangeDocs  
    | where ObjectClass == Identity // Identity
    and TableName == ProfileChangeDoc // Profile Change Doc
    and TypeofChange_Item == Insert // Insert 
    | extend Profile = ChangedTableKey
    | extend UserAssigned = ObjectID;
let UnitedProfiles =
toscalar(union fixedProfile, SensitiveProfiles
| summarize Profiles = make_list(Profile));
// Query logic
SAPAuditLog 
| where TimeGenerated {TimeSelect}
| where (SystemID in ({Systems}) or  '*' in ({Systems}))
| where MessageID in (AuditClasses)
| summarize by TimeGenerated, TerminalIPv6, User, Host, Email
| lookup kind = leftouter (IdentityChangeDocuments) on User
| where Profile in (UnitedProfiles)
| project-rename  TimeGenAudit = TimeGenerated1    
| where abs(datetime_diff('second',TimeGenerated,TimeGenAudit)) <= logsThreshold
or isnull(TimeGenAudit)
| project 
